/* global YUI */
/**
 * @author Fabrice Clerc
 */
YUI.add("wegas-escapegamev1-phone", function(Y){
    "use strict";
    var BOUNDINGBOX = "boundingBox", 
    CONTENTBOX = "contentBox",
    Phone;

    Phone = Y.Base.create("wegas-escapegamev1-phone", Y.Widget, [Y.WidgetParent, Y.WidgetChild, Y.Wegas.Editable, Y.Wegas.Parent], {
        CONTENT_TEMPLATE:"<div id='phone-main'></div>",
        renderUI: function(){
            //Initialisation of the buttons - name = fileName.mp3
            var cbox =  this.get(BOUNDINGBOX);
            var sound1 = "<button type='button' class='phone-player main-button' name='theSuitcase_Morse.mp3'>Play</button><br />";
            var sound2 = "<button type='button' class='phone-player main-button' name='theSuitcase_SacredNature.mp3'>Play</button><br />";
            var sound3 = "<button type='button' class='phone-player main-button' name='theSuitcase_Nada.mp3'>Play</button>";
            cbox.append(sound1).append(sound2).append(sound3);
        },
        bindUI: function(){

            Y.all(".phone-player").on("click", function (e) {
                //Get the gameModelId
                 var gamemodel = Y.Wegas.Facade.GameModel.cache.getCurrentGameModel().get("id");

                var name = e.target.get('name');
                console.log(name);
                var audio, url;
                url = "rest/GameModel/"+ gamemodel +"/File/read/" + name;
                if (Y.Lang.isFunction(window.Audio)) {
                    audio = new Audio(url);
                    audio.play();

                    //If a song is playing, all the buttons are "disabled" during 30 seconds
                    Y.all(".phone-player").set('disabled',':disabled');

                    Y.later( 1000 * 30, window, function(){
                        Y.all(".phone-player").set('disabled','');
                    }, [], false);

                } else {
                    new Y.Wegas.Panel({
                        bodyContent: "<div class=''> <span class=\"fa fa-4x fa-bullhorn\"></span> <span>Please listen to that <a target=\"_blank\" href=\"" +
                                url +
                                "\">sound</a>. <br /><br /><p style=\"font-size: 0.6em;color: rgba(153, 153, 153, 0.99);\">(And, btw, upgrade your browser...)</p><span></div>"
                    }).render();
                };

            });
        }
    });
    Y.Wegas.Phone = Phone;
});

