/* global YUI */
/**
 * @author Fabrice Clerc
 */
YUI.add("wegas-escapegamev1-codedevise", function(Y) {
    "use strict";

    var BOUNDINGBOX = "boundingBox", 
    CONTENTBOX = "contentBox",
    SOLUTION = "260",
    CLICKBUTTON = null,
    CodeDevise;

    CodeDevise = Y.Base.create("wegas-escapegamev1-codedevise", Y.Widget, [Y.WidgetParent, Y.WidgetChild, Y.Wegas.Editable, Y.Wegas.Parent], {
        CONTENT_TEMPLATE:"<div><h1>Code</h1><input type='text' id='impactvariable' class='convert-input'/></div>",
        renderUI: function(){
            var cbox =  this.get(BOUNDINGBOX); 
            CLICKBUTTON = Y.Node.create('<div class="solution-button"> <input type="button" class="main-button" value="Valider" /></div>');
            cbox.append(CLICKBUTTON);

        },
        bindUI: function() {
            CLICKBUTTON.on('click', this.checkCode);
        },
        syncUI: function() {

            var instance = Y.Wegas.Facade.Variable.cache.find("name","converterLocked").getInstance();
            instance = instance.get("value");

            //If the code was already ok, the input become "disabled"
                if(instance === 0){
                     //Y.one('#impactvariable').addClass("hide-input");
                     Y.one('#impactvariable').set("disabled",":disabled");
                     Y.one('#impactvariable').addClass('solution-true');
                     Y.one('#impactvariable').set("value","----");
                }
        },
        checkCode: function(){
            var codeInput = Y.one('#impactvariable').get("value");

            if(codeInput === SOLUTION){
                Y.one('#impactvariable').addClass('solution-true');
                Y.one('#impactvariable').set("disabled",":disabled");

            var instance = Y.Wegas.Facade.Variable.cache.find("name","converterLocked").getInstance();
            instance.set("value", 0);
            Y.Wegas.Facade.Variable.cache.put(instance.toObject());

            } else{
                Y.one('#impactvariable').addClass("solution-false");
            }
        }
    });
    Y.Wegas.CodeDevise = CodeDevise;
});

