/* global YUI */
/**
 * @author Fabrice Clerc
 */

YUI.add("wegas-escapegamev1-converter", function(Y){
    "use strict";
    var BOUNDINGBOX = "boundingBox", 
    CONTENTBOX = "contentBox",
    EXCHANGERATE = [0.5, 2.5, 80], //Exchangerate - can be change here
    Converter;

    Converter = Y.Base.create("wegas-escapegamev1-converter", Y.Widget, [Y.WidgetParent, Y.WidgetChild, Y.Wegas.Editable, Y.Wegas.Parent], {
        CONTENT_TEMPLATE:"<div id='converter-display'></div>",

        renderUI: function(){
            // The "name" of the elements below define the exchangerate
            var devise1 = "<h2>Blue Traveli -> Gold Traveli: </h2><div><input class='devise devise1 convert-input' value='0' name='1'/><div><div class='result1 convert-result'></div>";
            var devise2 = "<h2>Yellow Traveli -> Gold Traveli: </h2><div><input class='devise devise2 convert-input' value='0' name='2'/><div><div class='result2 convert-result'></div>";
            var devise3 = "<h2>Green Traveli -> Gold Traveli: </h2><div><input class='devise  devise3 convert-input' value='0' name='3'/><div><div class='result3 convert-result'></div>";
            var cbox =  this.get(BOUNDINGBOX);
            cbox.append(devise1).append(devise2).append(devise3);
        },
        bindUI:function(){

            Y.all('.devise').on('keyup', function (ev) {
                var name = ev.target.get("name");
                Y.one('.result'+name).setHTML("");
                var valueToConvert = ev.target.get("value");
                var valueConverted = Math.round(valueToConvert / EXCHANGERATE[name-1]*100)/100 ;
                //Check if the value is not a number
                if(isNaN(valueConverted)){
                    Y.one('.result'+name).setHTML("Veuillez entrer un nombre"); // Message if it's not a number
                } else {
                    Y.one('.result'+name).setHTML(valueConverted);
                }
            }, this.syncUI, this);
        },
        syncUI:function(){
            for (var i = 0, max = 3; i < max; i++) {
                var j = Y.one(".devise"+(i+1)).get("value");
                Y.one(".result"+(i+1)).setHTML(j);
            }
            var i = Y.one(".devise1").get("value");
            Y.one(".result1").setHTML(i);
        }
    });
    Y.Wegas.Converter = Converter;
});

