/* global YUI */
/**
 * @author Fabrice Clerc
 */

YUI.add("wegas-escapegamev1-calculatordisplay", function(Y){
    "use strict";
    var BOUNDINGBOX = "boundingBox", 
    CONTENTBOX = "contentBox",
    CalculatorDisplay;

    CalculatorDisplay = Y.Base.create("wegas-escapegamev1-calculatordisplay", Y.Widget, [Y.WidgetParent, Y.WidgetChild, Y.Wegas.Editable, Y.Wegas.Parent], {
        CONTENT_TEMPLATE:"<div class='top'><span class='clear'>C</span><div class='screen'></div></div><div class='keys'><span>7</span><span>8</span><span>9</span><span class='operator'>+</span><span>4</span><span>5</span><span>6</span><span class='operator'>-</span><span>1</span><span>2</span><span>3</span><span class='operator'>/</span><span>0</span><span>.</span><span class='eval'>=</span><span class='operator'>*</span></div>",
        renderUI: function(){

        },
        bindUI: function(){
            //Variable fonction
            var   first = "";
            var   second = "";
            var   opp = "";

            var keys = this.get(BOUNDINGBOX).all('span');
            keys.on('click', function(e){
                //Variable interractive
                var input = Y.one('.screen');
                var inputVal = input.getHTML(); 
                var btnVal = e.target.getHTML();

                //Check if the value of the button pressed is a number 
                if((btnVal === '1' || btnVal === '2' || btnVal === '3' || btnVal === '4' || btnVal === '5' || btnVal === '6' || btnVal === '7' || btnVal === '8' || btnVal === '9' || btnVal === '0')){
                    if (opp === ""){
                        first = inputVal + btnVal;
                        input.setHTML(first);

                    } else{
                        second = second + btnVal;
                        input.setHTML(second);
                    }
                };
                //Check if the value of the button pressed is an opperator
                if (btnVal === '-' || btnVal === '+' || btnVal === '*' || btnVal === '=' || btnVal === '/' ){
                    if(btnVal === '=' && first !== "" && second !=="" && opp !== "") {
                        DoSum();
                    } else {
                        opp = btnVal;
                    }
                };
                //Check if the value of the button pressed is "C" as Clear
                if (btnVal === 'C'){
                    console.log('C');
                    first = "";
                    second ="";
                    opp = "";
                    input.setHTML("");
                };
                //Do the sum
                function DoSum(Y){
                    console.log("DoSum", first, second, opp);
                    var result = 0;
                    if(first !== "" && second !== "" && opp !== "")
                    {
                        result = eval(first + opp + second);

                        input.setHTML(result);
                        first = result;
                        second = "";
                        opp = "";
                    }
                };
            });

        }
    });
    Y.Wegas.CalculatorDisplay = CalculatorDisplay;

});

