/* global YUI, clickButton, Variable, self, gameModel, gamemodel, Lang, Wegas, entity, ATTRS */
/**
 * @author Fabrice Clerc
 */

YUI.add("wegas-escapegamev1-finalsolution", function(Y) {
    "use strict";

    var BOUNDINGBOX = "boundingBox", 
    CONTENTBOX = "contentBox",
    CLICKBUTTON = null,
    SOLUTIONS = [{name:"Ville de desitnation: ",value:"paris"},{name:"Heure de départ: ",value:"16:00"},{name:"Prix en Traveli Gold: ",value:"311"},{name:"Porte d'embarquement: ",value:"A6"},{name:"N° du vol:", value:"ps49"}], //Nombre de solutions name: sol1, sol2, sol3...
    FinalSolution;

    FinalSolution = Y.Base.create("wegas-escapegamev1-finalsolution", Y.Widget, [Y.WidgetParent, Y.WidgetChild, Y.Wegas.Editable, Y.Wegas.Parent], {
        CONTENT_TEMPLATE:"<div id='final-solution'></div><div class='false-solution></div>'",

        renderUI: function(){
            var cbox =  this.get(BOUNDINGBOX);
            //Create inputs for the final solutions
            for (var i = 0, max = SOLUTIONS.length; i < SOLUTIONS.length; i++) {
                var solInput = "<div class='solution-name'>"+SOLUTIONS[i].name+"<input id='solution"+(i+1)+"'class='input-solution' type='text'/></div><br />";
                cbox.append(solInput);
            }
            CLICKBUTTON = Y.Node.create('<div class="solution-button"> <input type="button" class="main-button" value="Valider" /></div>');
            cbox.append(CLICKBUTTON);
        },
        bindUI: function() {
            CLICKBUTTON.on('click', this._handleButtonClick, this, CONTENTBOX);
        },
        _handleButtonClick:function(e){
            //Get all solutions of the inputs
            var solutionInput = [];
            for (var i = 0, max = SOLUTIONS.length; i < SOLUTIONS.length; i++) {
                var sol = Y.one("#solution"+(i+1)+"").get("value");
                solutionInput[i] = sol;
            }
            this.solutionTrue(solutionInput, e);
        },

        solutionTrue:  function(solutionInput, e) {
            var i = -1;
            var win = 0;
            do {
                i += 1;
                if (SOLUTIONS[i].value.toLowerCase() === solutionInput[i].toLowerCase()) {
                    Y.one('#solution'+(i+1)).addClass('solution-true'); // Add class "solution-true" if correct
                    win += 1;
                } else {
                    Y.one('#solution'+(i+1)).addClass('solution-false');// Add class "solution-false" if false
                }    
            } while (i < solutionInput.length-1);
            if(win === solutionInput.length) {
                 this.endGame();
            } else {
                return;
            }
        },

        endGame: function(){
            var instance = Y.Wegas.Facade.Variable.cache.find("name","solutionsTrue").getInstance();
            instance.set("value", 1);
            Y.Wegas.Facade.Variable.cache.put(instance.toObject());
        }
    }
            );
    Y.Wegas.FinalSolution = FinalSolution;
});

