/* global YUI */
/**
 * @author Fabrice Clerc
 */

YUI.add("wegas-escapegamev1-timingdisplay", function(Y){
    "use strict";
    var BOUNDINGBOX = "boundingBox", 
    CONTENTBOX = "contentBox",
    TimingDisplay;
    TimingDisplay = Y.Base.create("wegas-escapegamev1-timingdisplay", Y.Widget, [Y.WidgetParent, Y.WidgetChild, Y.Wegas.Editable, Y.Wegas.Parent], {
        CONTENT_TEMPLATE:"<div id='timing-display'></div>",
        renderUI: function(){
            function countdown(minutes) {
                var seconds = 60;
                var mins = minutes;
                if ( minutes <= 5){ // The countdown becomes red if time < 5
                    Y.one('#timing-display').addClass('timing-red');
                }
                function tick() {
                    var counter = Y.one('#timing-display');
                    var current_minutes = mins-1;
                    seconds--;
                    counter.setHTML(current_minutes.toString() + ":" + (seconds < 10 ? "0" : "") + String(seconds));
                    if( seconds >= 0 ) {
                        var win = Y.Wegas.Facade.Variable.cache.find("name","solutionsTrue").getInstance();
                        win = win.get("value");
                        if(win === 1){
                           saveTime(minutes);
                           counter.setHTML(current_minutes.toString() + ":" + (seconds < 10 ? "0" : "") + String(seconds));
                           return;
                        }
                        if (seconds === 0) {
                            saveTime(current_minutes);
                            if (minutes === 1){
                                counter.setHTML("00:00"); //Set HTML if the time is over
                                minutes = 0;
                                saveTime(minutes);
                                return;
                            }
                        }
                        setTimeout(tick, 1000);
                    } else {
                        if(mins > 1){

                            countdown(mins-1);           
                        }
                    };

                }
                tick();
            }

            function saveTime(minutes){
                var instance = Y.Wegas.Facade.Variable.cache.find("name","timerminutes").getInstance();
                instance.set("value", minutes);
                Y.Wegas.Facade.Variable.cache.put(instance.toObject());

            }
            //Get the value of the variable "TimerMinutes" and start the countdown
            var instanceminutes = Y.Wegas.Facade.Variable.cache.find("name","timerminutes").get("value");
            if (instanceminutes === 0) {
                Y.one('#timing-display').setHTML("00:00").addClass('timing-red');
            } else {
                countdown(instanceminutes); 
            }
        },

        destructor: function() {
            this.destroy();
        }

    }
            );
    Y.Wegas.TimingDisplay = TimingDisplay;

});
