var chance, alea=1, transition = false, resInst = Variable.findByName(self.getGameModel(), 'resources').items.get(0).getInstance(self);
if(parseInt(resInst.getConfidence()) + parseInt(resInst.getProperty('bonusInducement'))>= 75||parseInt(resInst.getProperty('leadershipLevel'))>1){
if(resInst.getProperty('wishIsKnow')=='false') alea += 1;
if(resInst.getProperty('hateIsKnow')=='false') alea += 1;
if(resInst.getProperty('hiddenSkillsets')!='') alea += 1;
chance = Math.floor(Math.random()*alea)+1;
if(resInst.getProperty('wishIsKnow') == 'false' && chance <=1) transition = true
}
transition

