tasks = Variable.findByName(gm, 'tasks');
for(i=0;i<resInst.getAssignments().size();i++){
assignmentTaskId = resInst.getAssignments().get(i).getTaskDescriptorId();
for(j=0;j<tasks.items.size();j++){
tasksDesc = tasks.items.get(j);
if(tasksDesc.getInstance(self).getActive()==true  && tasksDesc.getId() == assignmentTaskId){
free = false;
break;
}
}
for(j=0;j<absences.items.size();j++){
absenceDesc = absences.items.get(j);
if(absenceDesc.getInstance(self).getActive()==true && absenceDesc.getId() == assignmentTaskId){
free = false;
break;
}
}
}
free;

