Default.content:
/* Layout */
.wegas-playerview {
    font-family: Arial, Helvetica, sans-serif;
}
.wegas-playerview > .wegas-pageloader-content {
    background: url(wegas-cep/images/templatemo_site_title_bg_wrapper.jpg) repeat-x 0 0,
    url(wegas-cep/images/templatemo_banner_bg_wrapper.jpg) repeat-x 0 100px;
}
.wegas-playerview > .wegas-pageloader-content:after {
    content: " Â© 2014 Albasim";
    position: absolute;
    bottom: 0px;
    height: 50px;
    width: 100%;
    background: url(wegas-cep/images/templatemo_content_bg.jpg) top repeat-x;
    font-size: 11px;
    text-align: center;
    padding-top: 15px;
}

/* Period/phases */
.wegas-template-valuebox-unit {
    float:left;
    padding: 0px 4px;
    color: white;
    margin: 1px 0;
    border:none;
    border-right: 1px solid lightgray;
    background: none;
}
.wegas-template-valuebox-unit:last-child {
    border-right:none;
}
.wegas-template-valuebox-selected {
    background-color:white;
    color: black;
}

.wegas-template-valuebox label {
    display: inline-block;
    vertical-align: top;
    margin-top: 5px;
    margin-right: 5px;
}
.wegas-template-valuebox-units,
.wegas-template-title div {
    display: inline-block;
    padding:0px 2px;
    border: 1px solid #999;
    border-radius: 3px;
    background-color: #0981A9;
    margin-top: 3px;
    color: white;
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#80ffffff',endColorstr='#00ffffff',GradientType=0);
    background-image: -webkit-gradient(linear,0 0,0 100%,from(rgba(255, 255, 255, 0.30)),color-stop(40%,rgba(255, 255, 255, 0.15)),to(transparent));
    background-image: -webkit-linear-gradient(rgba(255, 255, 255, 0.30),rgba(255, 255, 255, 0.15) 40%,transparent);
    background-image: -moz-linear-gradient(top,rgba(255, 255, 255, 0.30),rgba(255, 255, 255, 0.15) 40%,transparent);
    background-image: -ms-linear-gradient(rgba(255, 255, 255, 0.30),rgba(255, 255, 255, 0.15) 40%,transparent);
    background-image: -o-linear-gradient(rgba(255, 255, 255, 0.30),rgba(255, 255, 255, 0.15) 40%,transparent);
    background-image: linear-gradient(rgba(255, 255, 255, 0.30),rgba(255, 255, 255, 0.15) 40%,transparent);
    -webkit-box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.25) inset,0 2px 0 rgba(255, 255, 255, 0.30) inset,0 1px 2px rgba(0, 0, 0, 0.15);
    -moz-box-shadow: 0 0 0 1px rgba(0,0,0,0.25) inset,0 2px 0 rgba(255,255,255,0.30) inset,0 1px 2px rgba(0,0,0,0.15);
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.25) inset,0 2px 0 rgba(255, 255, 255, 0.30) inset,0 1px 2px rgba(0, 0, 0, 0.15);
}
.wegas-template-title div {
    padding:2px 6px 1px;
}

/* Time cards */
.wegas-template-box label, .wegas-template-box-value {
    font-size: 11px;
}
.wegas-template-box-unit {
    width: 7px;
    height:10px;
    float:left;
    border: none;
    border-radius: 2px;
    background-color: #0981A9;
    margin: 1px 1px 0 0;
    color: white;
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#80ffffff',endColorstr='#00ffffff',GradientType=0);
    background-image: -webkit-gradient(linear,0 0,0 100%,from(rgba(255, 255, 255, 0.30)),color-stop(40%,rgba(255, 255, 255, 0.15)),to(transparent));
    background-image: -webkit-linear-gradient(rgba(255, 255, 255, 0.30),rgba(255, 255, 255, 0.15) 40%,transparent);
    background-image: -moz-linear-gradient(top,rgba(255, 255, 255, 0.30),rgba(255, 255, 255, 0.15) 40%,transparent);
    background-image: -ms-linear-gradient(rgba(255, 255, 255, 0.30),rgba(255, 255, 255, 0.15) 40%,transparent);
    background-image: -o-linear-gradient(rgba(255, 255, 255, 0.30),rgba(255, 255, 255, 0.15) 40%,transparent);
    background-image: linear-gradient(rgba(255, 255, 255, 0.30),rgba(255, 255, 255, 0.15) 40%,transparent);
    -webkit-box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.25) inset,0 2px 0 rgba(255, 255, 255, 0.30) inset,0 1px 2px rgba(0, 0, 0, 0.15);
    -moz-box-shadow: 0 0 0 1px rgba(0,0,0,0.25) inset,0 2px 0 rgba(255,255,255,0.30) inset,0 1px 2px rgba(0,0,0,0.15);
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.25) inset,0 2px 0 rgba(255, 255, 255, 0.30) inset,0 1px 2px rgba(0, 0, 0, 0.15);
}

/* Gauges */ 
.wegas-gauge-content {
    font-size: 10px;
}

/* Icons */
.cep-icon {
    display: inline-block;
    width: 20px;
    height: 19px;
    margin: -2px 4px 0 0;
    background-image: url(wegas-cep/images/cep-icons.png);
}
.cep-icon-questions {
    background-position:0 -86px;
}
.cep-icon-home {
    background-position:0 -157px;
}
.cep-icon-history {
    background-position:0 -121px;
}
.cep-icon-mail {
    background-position:0 -47px;
}
.cep-icon-employees {
    background-position:0 -245px;
}
.cep-icon-actions {
    background-position:0 -190px;
}


/* Team */
.wegas-cep-folder  {
    width: 99%;
    margin: 0 0.5%;
    margin-bottom: 20px;
}
/*selectors*/
.selectors {
    text-align:center;
    white-space:nowrap;
}
.selector {
    display:inline-block;
    width:100px;
    text-align: center;
    margin:10px;
    padding:5px;
    background: rgba(60, 100, 200, 0.1);
    box-shadow: 0px 0px 4px #6D6D6D;
    position: relative;
}
.wegas-cep-itemselector-content .yui3-scrollview{
    background:none;
}
.selector .cep-folder-talk {
    width:35px;
    height:29px;
    background: url(../../wegas-leaderway/images/talk-30.png) no-repeat;
    position: absolute;
    top: -6px;
    left: 15px;
}
.selectors .current{
    background: rgba(60, 100, 200, 0.3);
}
.selector:hover{
    box-shadow: 0px 0px 4px #000000;
    cursor:pointer;
}
.selector .nodeformatter-properties .label{
    display: none;
}
/*informations*/
.informations{
    background: rgba(60, 100, 200, 0.1);
    box-shadow: 0px 0px 4px #6D6D6D;
    padding:10px;
}
.informations .titre{
    font-weight: bold;
    margin: 10px 0;
}
.informations .picture{
    position:absolute;
    margin-left:785px;
    background-color: lightgray;
    border:1px solid darkgray;
}
.informations .nodeformatter-properties{
    margin:10px 0;
    max-width: 760px;
}
.informations .nodeformatter-properties span{
    display:inline-block;
    vertical-align: top;
}
.informations .nodeformatter-valuebox {
    clear:both;
    height:15px;
    margin:10px 0;
}
.informations .nodeformatter-valuebox .label,
.informations .nodeformatter-valuebox .box-units,
.informations .nodeformatter-valuebox .box-value{
    float:left;
}
.informations .label{
    width:80px;
}
.informations .nodeformatter-valuebox .box-units{
    display:block;
    background: rgba(60, 100, 200, 0.3);
    width:200px;
    height:15px;
    font-size: 1em;
    border: darkgray solid 1px;
    overflow: hidden;
}
.informations .nodeformatter-valuebox .box-unit{
    display:block;
    background-color:#6F89A2;
    background-image:url(../../wegas-cep/images/gaugeFiller.png);
    background-repeat: repeat-x;
    float:left;
    width:2px;
    height:15px;
}
.informations .nodeformatter-valuebox .box-value{
    margin-left: 5px;
    font-size: 0.8em;
}
.informations .nodeformatter-position{
    margin:10px 0;
}
.informations li{
    color:#6F89A2;
    max-width: 759px;
    margin-left:15px;
}
.informations .nodeformatter-position .previous{
    color:black;
}
.informations .nodeformatter-position .current{
    list-style-type:circle;
    color:black;
}

.wegas-cep-folder .yui3-button {
    color: rgb(255, 255, 255);
    background-color: rgb(175, 3, 3);
    padding: 7px 12px;
    font-weight: bold;
    text-shadow: rgb(211, 72, 72) 1px 2px 1px;
    position: absolute;
    top: 285px;
    right: 24px;
    width: 152px;
}

.wegas-panel-content {
    padding: 20px;
}
.cep-folder-panel .cep-folder-picture {
    float: left;
    border: 1px solid darkgray;
    background: white;
    height: 250px;
    width: 285px;
}
.cep-folder-panel .wegas-simpledialogue {
    width: 562px;
    float: right;
}
.cep-folder-panel .talk {
    text-align: left;
    margin-top: 20px;
    margin-bottom: 40px;
}
.cep-folder-panel .response {
    text-align: right;
}
.cep-folder-panel .response li {
    margin-bottom: 8px;
    cursor: pointer;
}
.cep-folder-panel .response li:hover {
    text-decoration: underline;
}

